#include <iostream>
#include <vector>
using namespace std;
int firstUniqChar(string s)
{
    int a[26] = {0};
    for (int i = 0; i < s.size(); i++)
    {
        a[s[i] - 'a']++;
    }
    vector<int> index;
    for (int i = 0; i < 26; i++)
    {
        if (a[i] == 1)
        {
            char temp = 'a' + i;
            for (int i = 0; i < s.size(); i++)
            {
                if (temp == s[i])
                {
                    index.push_back(i);
                }
            }
        }
    }
    if (index.empty())
        return -1;
    else
    {
        int min = index[0];
        for (int i = 1; i < (int)index.size(); i++)
        {
            if (index[i] < min)
                min = index[i];
        }
        return min;
    }
}
int main()
{
    string s1 = "leetcode";
    string s2 = "loveleetcode";
    string s3 = "";

    cout << firstUniqChar(s3) << '\n';
    return 0;
}